package com.tnsif.basic_java_programs;


public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
        if(x != 5) {
        	System.out.println("Value of x is not 5");
        }
       
        else {
        	System.out.println("Value of x is 5");
        }
	}

	
}


